import React, { Component, useState } from "react";
import api from '../api';
import DatePicker from 'react-datepicker';
import moment from 'moment-timezone';

import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";

function CreateArea() {
  const [title, setTitle] = useState();
  const [date, setDate] = useState();
  const [content, setContent] = useState();


  async function handleChangeTitle(event) {
      const title = event.target.value
      setTitle({ title })
  };

  async function handleChangeInputContent(event) {
      const content = event.target.value
      setContent({ content })
  };

  async function handleChangeDate(event) {
    setDate({ date: event })
};

async function addNote() {
      const { title, content, date  } = this.state;
      let mainDate = this.state.date;
      const taipei = moment.tz(mainDate, "Asia/Taipei");
      let dateObj = taipei.utc(true).format("L");
      const payload = { title, content, date: dateObj }

      await api.createNote(payload).then(res => {
          window.alert(`Note added successfully`)
          this.setState({
            title: '',
            date: '',
            content: '',
          })
      })
      window.location.reload();
  }

      return (
        <div>
        <form className="create-note">
            <input
              name="title"
              onChange={this.handleChangeTitle}
              value={title}
              placeholder="Title"
            />
  
          <DatePicker
            selected={this.state.date}
            onChange={this.handleChangeDate}
            name="startDate"
            format="format"
            dateFormat="MM/dd/yyyy"
            />

          <textarea
            name="content"
            onChange={this.handleChangeInputContent}
            value={content}
            placeholder="Take a note..."
          />
            <Fab onClick={this.addNote}>
              <AddIcon />
            </Fab>
        </form>
      </div>
      )
  };



export default CreateArea;
